mapper.delete:
DELETE FROM wishlist WHERE product_code = #{productCode}
AND mem_code = #{memCode} AND guest_code IS NULL
AND guest_code = #{guestCode} AND mem_code IS NULL

mapper.select:
SELECT COUNT(*) FROM wishlist WHERE product_code = #{productCode}
AND mem_code = #{memCode} AND guest_code IS NULL
AND guest_code = #{guestCode} AND mem_code IS NULL
