mapper.select:
select delivery_request
from orders
where order_code = #{orderCode}

WITH top_product AS ( SELECT oi.order_code, oi.product_code, p.product_name, p.product_id, ROW_NUMBER() OVER ( PARTITION BY oi.order_code ORDER BY oi.order_item_code ASC ) AS rn FROM order_items oi JOIN product p ON p.product_code = oi.product_code ), latest_delivery AS ( SELECT * FROM ( SELECT d.*, ROW_NUMBER() OVER ( PARTITION BY d.order_code ORDER BY COALESCE(d.updated_at, d.created_at) DESC, d.delivery_id DESC ) AS rn FROM delivery d ) x WHERE x.rn = 1 ), item_kinds AS ( SELECT order_code, COUNT(DISTINCT product_code) AS product_type_count FROM order_items GROUP BY order_code ) SELECT o.order_code AS orderCode, o.order_id AS orderId, o.create_at AS createAt, o.payment_method AS paymentMethod, o.orderer_name AS ordererName, o.orderer_phone AS ordererPhone, o.orderer_email AS ordererEmail, o.receiver_name AS receiverName, o.receiver_phone AS receiverPhone, o.final_amount AS finalAmount, o.delivery_fee AS deliveryFee, o.discount_amount AS discountAmount, o.total_amount AS totalAmount, o.receiver_zip AS receiverZip, o.receiver_addr AS receiverAddr, o.receiver_addr_detail AS receiverAddrDetail, o.delivery_request AS deliveryRequest, tp.product_code AS productCode, tp.product_name AS productName, COALESCE(ik.product_type_count, 0) AS productTypeCount, ld.tracking_number AS trackingNumber, pl.created_at AS payCreateAt, CASE WHEN o.payment_status = 'PENDING' THEN 'PAYPENDING' WHEN o.payment_status = 'PAID' AND o.simple_status = 'PAID' THEN 'PAID' WHEN o.payment_status = 'PAID' AND o.simple_status = 'SHIPPING' AND ld.delivery_status = 'SHIPPING' THEN 'SHIPPING' WHEN o.payment_status = 'PAID' AND o.simple_status = 'DELIVERED' AND ld.delivery_status = 'DELIVERED' THEN 'DELIVERED' WHEN o.payment_status = 'PAID' AND o.simple_status = 'PENDING' THEN 'ORDERPENDING' WHEN o.payment_status = 'CANCELLED' THEN 'CANCELLED' WHEN o.payment_status = 'REFUNDED' THEN 'REFUNDED' ELSE 'UNKNOWN' END AS status FROM orders o LEFT JOIN top_product tp ON o.order_code = tp.order_code AND tp.rn = 1 LEFT JOIN latest_delivery ld ON o.order_code = ld.order_code LEFT JOIN item_kinds ik ON o.order_code = ik.order_code LEFT JOIN payment_log pl ON o.order_code = pl.order_code WHERE 1=1 AND (o.order_status IS NULL OR o.order_status <> 'REFUND_PENDING')
AND o.order_id = #{searchParams.orderIdFilter}
AND o.orderer_name LIKE CONCAT('%', #{searchParams.ordererNameFilter}, '%')
AND EXISTS ( SELECT 1 FROM order_items oi2 JOIN product p2 ON oi2.product_code = p2.product_code WHERE oi2.order_code = o.order_code AND p2.product_name LIKE CONCAT('%', #{searchParams.productNameFilter}, '%') )
AND DATE(o.create_at) >= #{searchParams.startDate}
AND DATE(o.create_at) <= #{searchParams.endDate}
AND o.payment_method = #{searchParams.paymentMethodFilter}
AND o.payment_status = 'PENDING'
AND o.payment_status = 'PAID' AND o.simple_status = 'PAID'
AND o.payment_status = 'PAID' AND o.simple_status = 'PENDING'
AND o.payment_status = 'PAID' AND o.simple_status = 'SHIPPING' AND ld.delivery_status = 'SHIPPING'
AND o.payment_status = 'PAID' AND o.simple_status = 'DELIVERED' AND ld.delivery_status = 'DELIVERED'
ORDER BY o.create_at DESC
LIMIT #{searchParams.offset}, #{searchParams.limit}

SELECT COUNT(DISTINCT o.order_code) FROM orders o LEFT JOIN ( SELECT DISTINCT oi.order_code, p.product_name FROM order_items oi JOIN product p ON oi.product_code = p.product_code ) product_info ON o.order_code = product_info.order_code LEFT JOIN ( SELECT * FROM ( SELECT d.*, ROW_NUMBER() OVER ( PARTITION BY d.order_code ORDER BY COALESCE(d.updated_at, d.created_at) DESC, d.delivery_id DESC ) AS rn FROM delivery d ) x WHERE x.rn = 1 ) ld ON o.order_code = ld.order_code WHERE 1=1
AND o.order_id = #{searchParams.orderIdFilter}
AND o.orderer_name LIKE CONCAT('%', #{searchParams.ordererNameFilter}, '%')
AND EXISTS ( SELECT 1 FROM order_items oi2 JOIN product p2 ON oi2.product_code = p2.product_code WHERE oi2.order_code = o.order_code AND p2.product_name LIKE CONCAT('%', #{searchParams.productNameFilter}, '%') )
AND DATE(o.create_at) >= #{searchParams.startDate}
AND DATE(o.create_at) <= #{searchParams.endDate}
AND o.payment_method = #{searchParams.paymentMethodFilter}
AND o.payment_status = 'PENDING'
AND o.payment_status = 'PAID' AND o.simple_status = 'PAID'
AND o.payment_status = 'PAID' AND o.simple_status = 'PENDING'
AND o.payment_status = 'PAID' AND o.simple_status = 'SHIPPING' AND ld.delivery_status = 'SHIPPING'
AND o.payment_status = 'PAID' AND o.simple_status = 'DELIVERED' AND ld.delivery_status = 'DELIVERED'

SELECT o.order_id AS orderNumber, o.delivery_request AS deliveryMessage, o.total_amount AS totalAmountBeforeDiscount, o.final_amount AS finalAmount, p.product_id AS productId, p.product_name AS productName, CASE WHEN po.option_code IS NOT NULL THEN CONCAT(po.option_name, ': ', po.option_value) ELSE NULL END AS optionName, oi.quantity AS quantity, o.receiver_name AS receiverName, o.receiver_phone AS receiverPhone, o.receiver_zip AS receiverZip, o.receiver_addr AS receiverAddr, o.receiver_addr_detail AS receiverAddrDetail, CASE WHEN o.payment_status = 'PENDING' THEN '결제 대기' WHEN o.payment_status = 'PAID' THEN '결제 완료' ELSE o.payment_status END AS paymentStatus, CASE WHEN o.payment_method = 'card' THEN '카드' WHEN o.payment_method = 'bank' THEN '무통장입금' ELSE o.payment_method END AS paymentMethod, c.coupon_id AS couponId, d.discount_id AS discountCodeId, delivery.shipped_at AS shippingStartDate, o.create_at AS orderDate FROM orders o INNER JOIN order_items oi ON o.order_code = oi.order_code INNER JOIN product p ON oi.product_code = p.product_code LEFT JOIN product_option po ON oi.option_code = po.option_code LEFT JOIN ( SELECT DISTINCT d.order_code, d.shipped_at FROM delivery d WHERE d.delivery_status IN ('SHIPPING', 'DELIVERED') ) delivery ON o.order_code = delivery.order_code LEFT JOIN ( SELECT cm.order_code, c.coupon_id FROM coupon_member cm INNER JOIN coupon c ON cm.coupon_code = c.coupon_code WHERE cm.used = 1 ) c ON o.order_code = c.order_code LEFT JOIN ( SELECT dcu.order_code, d.discount_id FROM discount_code_usage dcu INNER JOIN discount d ON dcu.discount_code = d.discount_code ) d ON o.order_code = d.order_code WHERE 1=1 AND o.payment_status NOT IN ('FAILED', 'CANCELLED', 'REFUNDED') AND (o.order_status IS NULL OR o.order_status NOT IN ('CANCELLED', 'REFUNDED', 'RETURN_REQUESTED', 'REFUND_PENDING'))
AND DATE(o.create_at) >= #{startDate}
AND DATE(o.create_at) <= #{endDate}
ORDER BY o.create_at DESC, o.order_code ASC, oi.order_item_code ASC

select orders.orderer_name as ordererName
from orders
where order_code = #{orderCode}

select orders.orderer_phone as ordererPhone
from orders
where order_code = #{orderCode}
