mapper.select:
select cd_code as cdCode, cd_name as cdName
from hollybam.category_detail
where cate_code = #{categoryCode}

SELECT p.product_code as productCode, p.product_name as productName, p.product_id as productId, pr.price_original as productOriginal, pr.price_selling as productSelling, i.image_url as productImage FROM product p LEFT JOIN price pr ON p.product_code = pr.product_code LEFT JOIN image i ON p.product_code = i.product_code AND i.image_type = 'title' WHERE 1 = 1
AND p.cate_code = #{cateCode}
AND p.cd_code = #{cdCode}
AND ( p.product_name LIKE CONCAT('%', #{keyword}, '%') OR p.product_id LIKE CONCAT('%', #{keyword}, '%') )

select price_original
from price
where product_code = #{productCode}
